      "props": { "label": "Warmup completed", "checked": { "$bindState": "/completed/warmup" } }
    },

    "main-lifts-card": { "type": "Card", "props": {}, "children": ["main-lifts-inner"] },
    "main-lifts-inner": {
      "type": "Stack",
      "props": { "gap": "md" },
      "children": [
        "main-lifts-heading",
        "shoulder-alert",
        "variation-row",
        "main-strength-table",
        "main-volume-table",
        "deadlift-cue-alert",
        "main-lifts-done"
      ]
    },
    "main-lifts-heading": {
      "type": "Heading",
      "props": { "level": 3, "text": "Main Lifts · ~25 min" }
    },
    "shoulder-alert": {
      "type": "Alert",
      "props": {
        "title": "Shoulder impingement protocol",
        "description": "No overhead pressing today. Face pulls: rope at eye level, neutral grip, drive elbows back with full external rotation. Stop any exercise that causes anterior shoulder pain.",
        "variant": "warning"
      }
    },
    "variation-row": {
      "type": "Stack",
      "props": { "direction": "row", "gap": "md" },
      "children": ["variation-switch", "variation-desc"]
    },
    "variation-switch": {
      "type": "Switch",
      "props": { "label": "Strength mode", "checked": { "$bindState": "/strengthMode" } }
    },
    "variation-desc": {
      "type": "Text",
      "props": {
        "text": {
          "$cond": { "$state": "/strengthMode" },
          "$then": "Pyramid — 85–90% 1RM, low reps",
          "$else": "Hypertrophy — 70–75% 1RM, higher reps"
        },
        "variant": "muted"
      }
    },
    "main-strength-table": {
      "type": "Table",
      "props": {
        "columns": [
          { "key": "exercise", "header": "Exercise" },
          { "key": "sets", "header": "Sets × Reps" },
          { "key": "load", "header": "Load" },
          { "key": "rest", "header": "Rest" },
          { "key": "rpe", "header": "RPE" }
        ],
        "rows": [
          { "exercise": "Conventional Deadlift", "sets": "1×5 / 1×3 / 1×1 / 1×2+", "load": "110 / 120 / 127.5 kg", "rest": "3–4 min", "rpe": "7 / 8 / 9 / 8" },
          { "exercise": "Barbell Pendlay Row", "sets": "3×5", "load": "75 kg", "rest": "2.5 min", "rpe": "8" }
        ]
      },
      "visible": { "eq": [{ "$state": "/strengthMode" }, true] }
    },
    "main-volume-table": {
      "type": "Table",
      "props": {
        "columns": [
          { "key": "exercise", "header": "Exercise" },
          { "key": "sets", "header": "Sets × Reps" },
          { "key": "load", "header": "Load" },
          { "key": "rest", "header": "Rest" },
          { "key": "rpe", "header": "RPE" }
        ],
        "rows": [
          { "exercise": "Conventional Deadlift", "sets": "4×6", "load": "105 kg", "rest": "3 min", "rpe": "7–8" },
          { "exercise": "Barbell Pendlay Row", "sets": "4×8", "load": "65 kg", "rest": "2 min", "rpe": "7" }
        ]
      },
      "visible": { "eq": [{ "$state": "/strengthMode" }, false] }
    },
    "deadlift-cue-alert": {
      "type": "Alert",
      "props": {
        "title": "Deadlift cue",
        "description": "Lats tight before the pull — 'protect your armpits'. Bar over mid-foot. Drive the floor away; hips and shoulders rise at the same rate.",
        "variant": "info"
      }
    },
    "main-lifts-done": {
      "type": "Checkbox",
      "props": { "label": "Main lifts completed", "checked": { "$bindState": "/completed/mainLifts" } }
    },

    "accessories-card": { "type": "Card", "props": {}, "children": ["accessories-inner"] },
    "accessories-inner": {
      "type": "Stack",
      "props": { "gap": "md" },
      "children": ["accessories-heading", "accessories-table", "accessories-accordion", "accessories-done"]
    },
    "accessories-heading": {
      "type": "Heading",
      "props": { "level": 3, "text": "Accessories · Supersets · ~15 min" }
    },
    "accessories-table": {
      "type": "Table",
      "props": {
        "columns": [
          { "key": "superset", "header": "Superset" },
          { "key": "exercise", "header": "Exercise" },
          { "key": "sets", "header": "Sets" },
          { "key": "reps", "header": "Reps" },
          { "key": "rest", "header": "Rest" },
          { "key": "rpe", "header": "RPE" }
        ],
        "rows": [
          { "superset": "A", "exercise": "Lat Pulldown (neutral grip)", "sets": "3", "reps": "10–12", "rest": "90 sec after pair", "rpe": "7–8" },
          { "superset": "A", "exercise": "Hammer Curl (cable)", "sets": "3", "reps": "12–15", "rest": "—", "rpe": "7" },
          { "superset": "B", "exercise": "Cable Seated Row (close grip)", "sets": "3", "reps": "10–12", "rest": "90 sec after pair", "rpe": "7–8" },
          { "superset": "B", "exercise": "Face Pull (rope, eye level)", "sets": "3", "reps": "15–20", "rest": "—", "rpe": "6–7" }
        ]
      }
    },
    "accessories-accordion": {
      "type": "Accordion",
      "props": {
        "items": [
          {
            "title": "Exercise swaps & alternatives",
            "content": "Conventional Deadlift → Trap Bar Deadlift (more quad-friendly, easier on lower back) or Romanian Deadlift (hamstring isolation, lower CNS demand). Barbell Pendlay Row → Single-Arm Dumbbell Row (unilateral, neutral spine). Lat Pulldown → Assisted Pull-Up (progress toward weighted pull-ups). Face Pull → Band External Rotation if any anterior shoulder discomfort."
          }
        ]
      }
    },
    "accessories-done": {
      "type": "Checkbox",
      "props": { "label": "Accessories completed", "checked": { "$bindState": "/completed/accessories" } }
    },

    "cooldown-card": { "type": "Card", "props": {}, "children": ["cooldown-inner"] },
    "cooldown-inner": {
      "type": "Stack",
      "props": { "gap": "md" },
      "children": ["cooldown-heading", "cooldown-table", "cooldown-done"]
    },
    "cooldown-heading": {
      "type": "Heading",
      "props": { "level": 3, "text": "Cooldown · ~5 min" }
    },
    "cooldown-table": {
      "type": "Table",
      "props": {
        "columns": [
          { "key": "exercise", "header": "Exercise" },
          { "key": "duration", "header": "Duration" }
        ],
        "rows": [
          { "exercise": "Lat overhead stretch (doorway or low cable)", "duration": "60 sec / side" },
          { "exercise": "Hip flexor stretch (kneeling lunge)", "duration": "60 sec / side" },
          { "exercise": "Supine hamstring stretch", "duration": "60 sec / side" }
        ]
      }
    },
    "cooldown-done": {
      "type": "Checkbox",
      "props": { "label": "Cooldown completed", "checked": { "$bindState": "/completed/cooldown" } }
    }
  },
  "state": {
    "/strengthMode": true,
    "/completed/warmup": false,
    "/completed/mainLifts": false,
    "/completed/accessories": false,
    "/completed/cooldown": false
  }
}
